mapper.select:
SELECT NOTICT_ID AS notictId, TITLE AS title, CONTENT AS contents, BOARD_WRITER AS boardWriter, CREATED_AT AS createAt
FROM board
WHERE NOTICT_ID = #{id}

mapper.update:
UPDATE board
SET TITLE = #{title}, CONTENT = #{contents}, BOARD_WRITER = #{boardWriter}
WHERE NOTICT_ID = #{notictId}
